DROP TABLE PLAYERTBL;
DROP TABLE TEAMTBL;
DROP TABLE HITTERTBL;
DROP TABLE PICTHERTBL;

CREATE TABLE PLAYERTBL 
(
  PLAYERNAME NVARCHAR2(10) NOT NULL 
, BIRTH DATE NOT NULL 
, TEAMNAME NVARCHAR2(15) NOT NULL 
, POSI NVARCHAR2(5) NOT NULL 
, DEBUT_YEAR NUMBER(4)
, CONSTRAINT PLAYERTBL_PK PRIMARY KEY 
  (
    PLAYERNAME 
  )
  ENABLE 
);

CREATE TABLE HITTERTBL 
(
  PLAYERNAME NVARCHAR2(10) NOT NULL 
, BATTING_AVERAGE NUMBER(10,3) NOT NULL 
, RBI NUMBER(3) NOT NULL 
, HOMERUN NUMBER(3) NOT NULL 
, STOLEN_BASE NUMBER(3) NOT NULL 
, CONSTRAINT HITTERTBL_PK PRIMARY KEY 
  (
    PLAYERNAME 
  )
  ENABLE 
);

CREATE TABLE PICTHERTBL 
(
  PLAYERNAME NVARCHAR2(10) NOT NULL 
, TOTAL_VICTORIES NUMBER(3) NOT NULL 
, EARNED_RUN NUMBER(5,2) NOT NULL 
, STRIKE_OUT NUMBER(3) NOT NULL 
, SAVES NUMBER(3) NOT NULL 
, CONSTRAINT PICTHERTBL_PK PRIMARY KEY 
  (
    PLAYERNAME 
  )
  ENABLE 
);

CREATE TABLE TEAMTBL 
(
  TEAMNAME NVARCHAR2(15) NOT NULL 
, WIN NUMBER(4) NOT NULL 
, LOSE NUMBER(4) NOT NULL 
, TIE NUMBER(4) NOT NULL 
, GAME_COUNT NUMBER(4) NOT NULL 
, CONSTRAINT TEAMTBL_PK PRIMARY KEY 
  (
    TEAMNAME 
  )
  ENABLE 
);


