--외래키
ALTER TABLE PLAYERTBL
ADD CONSTRAINT PLAYERTBL_FK1 FOREIGN KEY
(
  TEAMNAME 
)
REFERENCES TEAMTBL
(
  TEAMNAME 
)
ENABLE;



--뷰 생성
CREATE VIEW BALL.PLAYERTBL_VIEW AS SELECT PLAYERNAME, TEAMNAME, POSI FROM BALL.PLAYERTBL;

--JOIN사용하여 타자 선수들 출력
SELECT PLAYERTBL.PLAYERNAME, PLAYERTBL.TEAMNAME, PLAYERTBL.POSI, HITTERTBL.BATTING_AVERAGE, HITTERTBL.RBI, HITTERTBL.HOMERUN, HITTERTBL.STOLEN_BASE
FROM PLAYERTBL, HITTERTBL 
WHERE PLAYERTBL.PLAYERNAME = HITTERTBL.PLAYERNAME;

--JOIN사용하여 투수 선수들 출력
SELECT PLAYERTBL.PLAYERNAME, PLAYERTBL.TEAMNAME, PLAYERTBL.POSI, PICTHERTBL.TOTAL_VICTORIES, PICTHERTBL.EARNED_RUN, PICTHERTBL.STRIKE_OUT, PICTHERTBL.SAVES
FROM PLAYERTBL, PICTHERTBL 
WHERE PLAYERTBL.PLAYERNAME = PICTHERTBL.PLAYERNAME;
